class GameDrPill {
    constructor(id) {
        this.canvas = document.getElementById(id);
        this.context = this.canvas.getContext('2d');
    }

    start(size = 8) {
        this.field = [];
        for (let i = 0; i < size; i++) {
            this.field.push([]);
            for (let j = 0; j < size; j++) {
                this.field[i][j] = 0;
            }
        }
    }

}

export default GameDrPill;
