import React from 'react';
import Redirect from 'react-router-dom';
import history from '../../history';

import './Game.css';
import GameDrPill from './GameDrPill';

class Game extends React.Component {

    constructor(props) {
        super(props);
        this.game = null;
        this.state = { sec: 60 };
    }

    componentDidMount() {
        
        this.game = new GameDrPill({ 
            id: 'canvas', 
            callbacks: {
                endGame(score) { 
                    
                    /*<form action="/result"></form>;
                    console.log('score', score);
                    let newWin = window.open('/', 'this.results', 'width = 300', 'height = 600');
                    newWin.document.write('this.results');*/
                },
                timeTick: (sec) => {
                    this.setState({ sec: sec });
                }
            }
        });
    }

    render() {

        if (this.state.sec <= 1) {
            return <Redirect to='/result' />
        }

        return (
            <div className="Game">
                <div>
                    <div>колбы: 0</div>
                    <div>времечко: { this.state.sec }</div>
                    <div>пилюли: 0/20</div>
                </div>
                <canvas id='canvas'></canvas>
                <br />
                <form>
                    <button onClick={() => history.push('/')}>Home page</button>
                    <button onClick={() => history.push('/result')}>Results</button>
                </form>
            </div>
        );
    }
}

export default Game;
