import React from 'react';
import {Redirect} from 'react-router-dom';
import history from '../../history';

import './Game.css';
import GameDrPill from './GameDrPill';

class Game extends React.Component {

    constructor(props) {
        super(props);
        this.game = null;
        this.state = { sec: 60, score: 0, points: 0, maxPoints: 20 };
    }

    componentDidMount() {
        this.game = new GameDrPill({ 
            id: 'canvas', 
            callbacks: {
                endGame: (score) => { 
                    this.setState({ score });
                },
                timeTick: (sec) => {
                    this.setState({ sec });
                },
                addPills: (points) => {
                    if (this.state.points + points >= this.state.maxPoints) {
                        this.setState({ 
                            points: 0, 
                            maxPoints: this.state.maxPoints + 10,
                            score: this.score + 1,
                            sec: 60
                        });
                    } else {
                        this.setState({ points: this.state.points + points  });
                    }
                }
            }
        });
    }



    render() {

        if (this.state.sec <= 1) {
            return <Redirect to={ {
                pathname: '/result',
                state: { score: this.state.score }
            } }
            />
        }

        return (
            <div className="Game">
                <div>
                    <div>колбы: 0</div>
                    <div>времечко: { this.state.sec }</div>
                    <div>пилюли: {this.state.points}/{this.state.maxPoints}</div>
                </div>
                <canvas id='canvas'></canvas>
                <br />
                <form>
                    <button onClick={() => history.push('/')}>Home page</button>
                    <button onClick={() => history.push('/result')}>Results</button>
                </form>
            </div>
        );
    }
}

export default Game;
