class GameDrPill {
    constructor(id) {
        this.canvas = document.getElementById(id);
        this.context = this.canvas.getContext('2d');
        this.field = [];
    }

    start() {
        this.field = [];
        for (let i = 0; i < 8; i++) {
            this.field.push([]);
            for (let j = 0; j < 8; j++) {
                this.field[i][j] = 0;
            }
        }

        let count = 64;
        const pills = ['a', 'b', 'c', 'd', 'e'];
        while (count) {
            const i = pills[Math.floor(Math.random() * ((pills.length - 1) - 0 + 1))] * 8;
            const j = pills[Math.floor(Math.random() * ((pills.length - 1) - 0 + 1))] * 8;
            
            this.field[i][j] = count;
            count--;
        } 
    }

    move(i1, j1, i2, j2) {
        if (this.field[i1][j1] === 0 || this.field[i2][j2] === 0) {
            if ((i1 === i2 && Math.abs(j1 - j2) === 1) || 
                (Math.abs(i1 - i2) === 1 && j1 === j2)
            ) {
                let c = this.field[i1][j1];
                this.field[i1][j1] = this.field[i2][j2];
                this.field[i2][j2] = c;
                this.count++;
                return true;
            }
        }
        return false;
    }

}

export default GameDrPill;
